import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
// import axios from 'axios'; // Uncomment if you use axios

interface SubjectScoreLevel {
  subject: string;
  '<4': number;
  '4-6': number;
  '6-8': number;
  '>=8': number;
}

const mockData: SubjectScoreLevel[] = [
  { subject: 'Toán', '<4': 10, '4-6': 25, '6-8': 40, '>=8': 25 },
  { subject: 'Lý', '<4': 15, '4-6': 30, '6-8': 35, '>=8': 20 },
  { subject: 'Hóa', '<4': 8, '4-6': 20, '6-8': 45, '>=8': 27 },
  { subject: 'Văn', '<4': 20, '4-6': 35, '6-8': 30, '>=8': 15 },
  { subject: 'Anh', '<4': 5, '4-6': 15, '6-8': 50, '>=8': 30 },
];

const ScoreLevels: React.FC = () => {
  const [data, setData] = useState<SubjectScoreLevel[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchScoreStatistics = async () => {
      try {
        // const response = await axios.get('/api/scores/statistics'); // Replace with your actual API endpoint
        // setData(response.data);
        setData(mockData); // Using mock data for demonstration
      } catch (err) {
        setError('Error fetching score statistics.');
      } finally {
        setLoading(false);
      }
    };
    fetchScoreStatistics();
  }, []);

  if (loading) return <div style={{padding: '30px'}}>Loading score statistics...</div>;
  if (error) return <div style={{padding: '30px', color: 'red'}}>Error: {error}</div>;

  return (
    <div style={{ padding: '30px' }}>
      <h2 style={{ fontSize: '1.8rem', marginBottom: '20px', color: '#333' }}>Thống kê cấp độ điểm theo môn</h2>
      <div style={{ background: 'white', padding: '20px', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.04)' }}>
        <ResponsiveContainer width="100%" height={400}>
          <BarChart
            data={data}
            margin={{
              top: 20,
              right: 30,
              left: 20,
              bottom: 5,
            }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="subject" />
            <YAxis label={{ value: 'Số lượng học sinh', angle: -90, position: 'insideLeft' }} />
            <Tooltip />
            <Legend />
            <Bar dataKey="<4" stackId="a" fill="#ff7a7a" name="< 4 điểm" />
            <Bar dataKey="4-6" stackId="a" fill="#ffc107" name="4-6 điểm" />
            <Bar dataKey="6-8" stackId="a" fill="#82ca9d" name="6-8 điểm" />
            <Bar dataKey=">=8" stackId="a" fill="#4caf50" name=">= 8 điểm" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default ScoreLevels;
