import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import { Bar, Doughnut } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

interface ScoreLevel {
  level: string;
  count: number;
  percentage: number;
}

interface SubjectStatistics {
  subject: string;
  displayName: string;
  levels: ScoreLevel[];
  totalStudents: number;
  color: string;
}

const SubjectStatistics: React.FC = () => {
  const [statistics, setStatistics] = useState<SubjectStatistics[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchStatistics();
  }, []);

  const fetchStatistics = async () => {
    try {
      const response = await axios.get('http://localhost:9000/api/students/subject-statistics');
      
      if (response.data.success) {
        setStatistics(response.data.data);
      } else {
        setError(response.data.message || 'Failed to fetch statistics');
      }
    } catch (err) {
      setError('Error fetching subject statistics');
    } finally {
      setLoading(false);
    }
  };

  const chartData = {
    labels: statistics.map(stat => stat.displayName),
    datasets: [
      {
        label: '>=8 points',
        data: statistics.map(stat => stat.levels.find(l => l.level === '>=8 points')?.count || 0),
        backgroundColor: '#10b981',
      },
      {
        label: '6-8 points',
        data: statistics.map(stat => stat.levels.find(l => l.level === '6-8 points')?.count || 0),
        backgroundColor: '#3b82f6',
      },
      {
        label: '4-6 points',
        data: statistics.map(stat => stat.levels.find(l => l.level === '4-6 points')?.count || 0),
        backgroundColor: '#f59e0b',
      },
      {
        label: '<4 points',
        data: statistics.map(stat => stat.levels.find(l => l.level === '<4 points')?.count || 0),
        backgroundColor: '#ef4444',
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const,
      },
      title: {
        display: true,
        text: 'Student Distribution by Subject and Score Level',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  const totalStudentsData = {
    labels: statistics.map(stat => stat.displayName),
    datasets: [
      {
        data: statistics.map(stat => stat.totalStudents),
        backgroundColor: statistics.map(stat => stat.color),
        borderWidth: 2,
        borderColor: '#ffffff',
      },
    ],
  };

  const totalStudentsOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'bottom' as const,
      },
      title: {
        display: true,
        text: 'Total Students by Subject',
      },
    },
  };

  if (loading) {
    return (
      <div className="max-w-6xl mx-auto">
        <div className="card">
          <div className="flex items-center justify-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
            <span className="ml-2 text-gray-600">Loading statistics...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto">
      <div className="card">
        <h2 className="text-2xl font-bold text-gray-800 mb-6 font-rubik">
          Subject Statistics
        </h2>

        {error && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          <div>
            <Bar data={chartData} options={chartOptions} />
          </div>
          <div>
            <Doughnut data={totalStudentsData} options={totalStudentsOptions} />
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {statistics.map((subject) => (
            <div key={subject.subject} className="bg-white border rounded-lg p-6 shadow-sm">
              <h3 className="text-lg font-semibold text-gray-800 mb-4" style={{ color: subject.color }}>
                {subject.displayName}
              </h3>
              
              <div className="mb-4">
                <p className="text-sm text-gray-600">Total Students</p>
                <p className="text-2xl font-bold" style={{ color: subject.color }}>
                  {subject.totalStudents.toLocaleString()}
                </p>
              </div>

              <div className="space-y-3">
                {subject.levels.map((level) => (
                  <div key={level.level} className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">{level.level}</span>
                    <div className="text-right">
                      <p className="text-sm font-medium text-gray-900">
                        {level.count.toLocaleString()}
                      </p>
                      <p className="text-xs text-gray-500">
                        {typeof level.percentage === 'number' ? level.percentage.toFixed(1) : '0.0'}%
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SubjectStatistics;
