import React from 'react';
import './App.css';

function App() {
  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
      {/* Header */}
      <div style={{ background: '#1428a0', color: 'white', padding: '20px 0', textAlign: 'center', fontSize: '2rem', fontWeight: 'bold' }}>
        G-Scores
      </div>
      <div style={{ display: 'flex', flex: 1 }}>
        {/* Sidebar */}
        <div style={{ width: '220px', background: 'linear-gradient(to bottom, #ffe259, #3a8dde)', color: '#222', padding: '30px 0 0 0', minHeight: '100%' }}>
          <div style={{ fontWeight: 'bold', fontSize: '1.3rem', marginBottom: '30px', textAlign: 'center' }}>Menu</div>
          <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
            <li style={{ padding: '12px 30px', cursor: 'pointer' }}>Dashboard</li>
            <li style={{ padding: '12px 30px', fontWeight: 'bold', background: 'rgba(0,0,0,0.07)', cursor: 'pointer' }}>Search Scores</li>
            <li style={{ padding: '12px 30px', cursor: 'pointer' }}>Reports</li>
            <li style={{ padding: '12px 30px', cursor: 'pointer' }}>Settings</li>
          </ul>
        </div>
        {/* Main Content */}
        <div style={{ flex: 1, background: '#f7f8fa', padding: '40px 30px' }}>
          {/* User Registration Box */}
          <div style={{ background: 'white', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.04)', padding: '28px 32px', marginBottom: '30px' }}>
            <div style={{ fontWeight: 'bold', fontSize: '1.1rem', marginBottom: '10px' }}>User Registration</div>
            <div style={{ marginBottom: '8px', fontSize: '0.97rem' }}>Registration Number:</div>
            <input type="text" placeholder="Enter registration number" style={{ padding: '7px 10px', border: '1px solid #ccc', borderRadius: '3px', marginRight: '10px', width: '220px' }} />
            <button style={{ padding: '7px 18px', background: '#111', color: 'white', border: 'none', borderRadius: '3px', cursor: 'pointer' }}>Submit</button>
          </div>
          {/* Detailed Scores Box */}
          <div style={{ background: 'white', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.04)', padding: '28px 32px' }}>
            <div style={{ fontWeight: 'bold', fontSize: '1.1rem', marginBottom: '10px' }}>Detailed Scores</div>
            <div style={{ fontSize: '0.97rem' }}>Detailed view of search scores here!</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
