import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
// import axios from 'axios'; // Uncomment if you use axios

interface ScoreLevel {
  level: string;
  count: number;
  percentage: number;
}

interface SubjectScoreLevel {
  subject: string;
  displayName: string;
  levels: ScoreLevel[];
  totalStudents: number;
  color: string;
}

interface ApiResponse {
  success: boolean;
  message: string;
  data: ScoreLevel[];
}

const ScoreLevels: React.FC = () => {
  const [data, setData] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchScoreStatistics = async () => {
      try {
        const response = await axios.get<ApiResponse>('http://localhost:9000/api/students/score-levels');
        console.log('API Response:', response.data); // Debug log
        
        if (response.data.success && response.data.data) {
          // Transform the data to match the chart's expected format
          const transformedData = response.data.data.map((item) => {
            const level = item.level.replace(' points', '');
            return {
              level: level,
              count: item.count,
              percentage: item.percentage
            };
          });
          
          console.log('Transformed Data:', transformedData); // Debug log
          setData(transformedData);
        } else {
          setError('Invalid data format received from API');
        }
      } catch (err) {
        console.error('Error fetching data:', err); // Debug log
        setError('Error fetching score statistics.');
      } finally {
        setLoading(false);
      }
    };
    fetchScoreStatistics();
  }, []);

  if (loading) return <div style={{padding: '30px'}}>Loading score statistics...</div>;
  if (error) return <div style={{padding: '30px', color: 'red'}}>Error: {error}</div>;
  if (!data || data.length === 0) return <div style={{padding: '30px'}}>No data available</div>;

  return (
    <div style={{ padding: '30px' }}>
      <h2 style={{ fontSize: '1.8rem', marginBottom: '20px', color: '#333' }}>Thống kê cấp độ điểm</h2>
      <div style={{ background: 'white', padding: '20px', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.04)' }}>
        <ResponsiveContainer width="100%" height={400}>
          <BarChart
            data={data}
            margin={{
              top: 20,
              right: 30,
              left: 20,
              bottom: 5,
            }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="level" />
            <YAxis label={{ value: 'Số lượng học sinh', angle: -90, position: 'insideLeft' }} />
            <Tooltip />
            <Legend />
            <Bar dataKey="count" fill="#4caf50" name="Số lượng" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default ScoreLevels;
